import { Container, Row, Col } from "react-grid-system";
import AccountCard from "../Components/AccountCard";
import { Title, ExplanationText, Explanation } from "../Components/Explanation";
import Link from "../Components/Link";
import createAccount from "../utils/createAccount";

const CreateAccount = ({ createCoin, accountList }) => {
  return (
    <Container className="wrapper">
      <Row>
        <Col>
          <Title>Let's learn about accounts!</Title>
          <Explanation>
            <ExplanationText>
              In most cryptos, it's very easy to make hundreds of accounts.
            </ExplanationText>
            <ExplanationText>
              Having a lot of accounts is useful for many reasons! You can have
              a cold storage account that you never touch and keep under your
              mattress, while also having a hot wallet account which you use to
              trade actively.
            </ExplanationText>
            <ExplanationText>
              Let's try creating a few accounts ourselves:
            </ExplanationText>
          </Explanation>
        </Col>
      </Row>

      {accountList?.length && <h1> Account List:</h1>}
      <Row style={{ marginBottom: "20px" }}>
        {accountList.map((account, index) => (
          <Col md={6} style={{ marginBottom: "20px" }}>
            <AccountCard account={account} />
          </Col>
        ))}
      </Row>
      <div
        style={{
          display: "flex",
          alignItems: "center",

          justifyContent: "space-between",
        }}
      >
        <div>
          <button
            onClick={() => {
              createCoin();
            }}
            className="createAccount"
          >
            Create Account
          </button>
        </div>

        <Link
          to={`/send-transactions`}
          className={!accountList.length ? "disabled" : null}
        >
          Go Next
        </Link>
      </div>
      <div
        style={{
          marginBottom: "100px",
        }}
      ></div>
    </Container>
  );
};

export default CreateAccount;
