const fetchAllAcountInfo = async (publicKey, connection) => {
  if (publicKey) {
    const balance = new Promise(connection.getBalance(publicKey));

    const accountInfo = new Promise(
      connection.getAccountInfoAndContext(publicKey),
    );

    return Promise.all(
      [balance, accountInfo].then((values) => {
        console.log(values);
        return values;
      }),
    );
  }

  return null;
};

export default fetchAllAcountInfo;
