import { Explanation, ExplanationText, Title } from "../Components/Explanation";
import { BrowserRouter as Router, Switch, Route, Link } from "react-router-dom";
import { useEffect, useState } from "react";
import { PublicKey } from "@solana/web3.js";
import fetchAllAcountInfo from "../utils/fetchAllAccountInfo";

const SendTransaction = ({ accountList, connection }) => {
  const [transaction, setTransactions] = useState(false);
  const [goToNextPage, setNextPage] = useState(false);

  const [value, setValue] = useState("");

  const getValue = (e) => {
    console.log(e);
  };
  useEffect(() => {
    const getNewAccount = async () => {
      const account = await new PublicKey(
        "DoFxSedewimW1j8wz1LDhNUsqWs97aytqXkhFj8WBz8H",
      );

      if (connection) {
        console.log(connection);
        const poop = await fetchAllAcountInfo(account, connection);
        console.log(poop);

        setTransactions(account);
      }
    };

    getNewAccount();
  }, [connection]);

  const transactAndAllowNextPage = () => {
    console.log(transaction);
  };

  return (
    <div>
      <Explanation>
        <Title>Let's try sending a transaction now!</Title>
        <ExplanationText>
          Being able to send and receive a transaction is really important. To
          do this, you need the reciever wallet, and the send wallet. This page
          has two generated accounts. If you want to check the transaction, you
          can see a list of all transactions here:
        </ExplanationText>
      </Explanation>

      <div>
        AS you can see there is a transaction fee associated with your
        transaction.
      </div>

      <AccountCard account={accountList?.[0]} />

      <AccountCard account={account} />

      <form>
        <input
          onChange={(e) => {
            setValue(e.target.value);
          }}
        ></input>
        <button type="submit"></button>
      </form>

      <Link
        to={`/learn-rent`}
        className={!accountList.length ? "disabled" : null}
      >
        Go Next
      </Link>
    </div>
  );
};

export default SendTransaction;
