import { useSpring, animated } from "react-spring";

const FadeIn = (WrappedComponent) => ({ ...rest }) => {
  const [springProps] = useSpring({
    opacity: 1,
    from: { opacity: 0 },
  });

  return (
    <animated.div style={springProps}>
      <WrappedComponent {...rest} />
    </animated.div>
  );
};

export default FadeIn;
