import { BrowserRouter as Router, Switch, Route, Link } from "react-router-dom";
import CreateAccount from "./Pages/01_CreateAccount";
import { useEffect, useRef, useState } from "react";

import "./App.css";
import createAccount from "./utils/createAccount";
import SendTransaction from "./Pages/02_SendTransactions";
import LearnAboutRent from "./Pages/03_LearnAboutRent";
import { Connection } from "@solana/web3.js";
import { url } from "./consts/consts";
import Particles from "react-particles-js";

const Home = () => {
  let ref = useRef();
  return (
    <div className="App">
      <header className="App-header">
        <Particles
          params={{
            particles: {
              number: {
                value: 20,
                density: {
                  enable: true,
                  value_area: 800,
                },
              },

              polygon: {
                enable: true,
                scale: 0.5,
                type: "inline",
                move: {
                  radius: 10,
                },
                url: "/small-deer.2a0425af.svg",
                inline: {
                  arrangement: "equidistant",
                },
                draw: {
                  enable: true,
                  stroke: {
                    color: "rgba(255, 255, 255, .2)",
                  },
                },
              },
              line_linked: {
                enable: false,
              },
              move: {
                speed: 1,
                out_mode: "out",
              },
              shape: {
                type: ["image", "circle"],
                image: [
                  {
                    src: "https://rpj.bembi.dev/react.cd2ab268.svg",
                    height: 20,
                    width: 23,
                  },
                  {
                    src: "https://rpj.bembi.dev/k8s.2d579d24.svg",
                    height: 20,
                    width: 20,
                  },
                  {
                    src: "https://rpj.bembi.dev/code.b3b4c4f4.png",
                    height: 20,
                    width: 20,
                  },
                ],
              },
              color: {
                value: "#CCC",
              },
              size: {
                value: 30,
                random: false,
                anim: {
                  enable: true,
                  speed: 4,
                  size_min: 10,
                  sync: false,
                },
              },
            },
            retina_detect: false,
          }}
        />
      </header>
    </div>
    // <div className="App">
    //   <header className="App-header">
    //     <div
    //       style={{
    //         padding: "20px",
    //       }}
    //     >
    //       Your Intro to Crypto and Defi! Learn how Proof of Stake works!
    //     </div>
    //     <Link to="/create-account">Get Started</Link>
    //   </header>
    // </div>
  );
};

function App() {
  const [accountList, setAccountList] = useState([]);

  const [connection, setConnection] = useState(null);

  useEffect(() => {
    const connectionToSolana = async () => {
      const conn = await new Connection(url, "recent");
      setConnection(conn);
    };
    connectionToSolana();
  }, []);

  const createCoin = async () => {
    const newAccount = await createAccount();
    setAccountList([...accountList, newAccount]);
  };

  return (
    <Router>
      <Switch>
        <Route path="/create-account">
          <CreateAccount
            connection={connection}
            createCoin={createCoin}
            accountList={accountList}
          />
        </Route>

        <Route path="/send-transactions">
          <SendTransaction
            connection={connection}
            createCoin={createCoin}
            accountList={accountList}
          />
        </Route>

        <Route path="/learn-rent">
          <LearnAboutRent
            connection={connection}
            createCoin={createCoin}
            accountList={accountList}
          />
        </Route>

        <Route path="/">
          <Home />
        </Route>
      </Switch>
    </Router>
  );
}

export default App;
