import { BrowserRouter as Router, Switch, Route, Link } from "react-router-dom";
import CreateAccount from "./Pages/01_CreateAccount";
import { useEffect, useRef, useState } from "react";

import "./App.css";
import createAccount from "./utils/createAccount";
import SendTransaction from "./Pages/02_SendTransactions";
import LearnAboutRent from "./Pages/03_LearnAboutRent";
import { Connection } from "@solana/web3.js";
import { url } from "./consts/consts";
import Particles from "react-particles-js";

const Home = () => {
  let ref = useRef();

  useEffect(() => {
    let canvas = ref.current;
    let context = canvas.getContext("2d");

    let img = new Image();

    img.src =
      "http://www.gravatar.com/avatar/a1f80339c0cef95be6dc73e0ac510d5d?s=32&d=identicon&r=PG";

    return () => {
      cancelAnimationFrame(requestId);
    };
  }, []);
  return (
    <div className="App">
      <header className="App-header">
        <Particles
          params={{
            fps_limit: 28,
            particles: {
              collisions: {
                enable: false,
              },
              number: {
                value: 200,
                density: {
                  enable: false,
                },
              },
              line_linked: {
                enable: true,
                distance: 30,
                opacity: 0.4,
              },
              move: {
                speed: 1,
              },
              opacity: {
                anim: {
                  enable: true,
                  opacity_min: 0.05,
                  speed: 1,
                  sync: false,
                },
                value: 0.4,
              },
            },
            polygon: {
              enable: true,
              scale: 0.5,
              type: "inline",
              move: {
                radius: 10,
              },
              url: "https://rpj.bembi.dev/github.e5dca3b6.png",
              inline: {
                arrangement: "equidistant",
              },
              draw: {
                enable: true,
                stroke: {
                  color: "rgba(255, 255, 255, .2)",
                },
              },
            },
            retina_detect: false,
            interactivity: {
              events: {
                onhover: {
                  enable: true,
                  mode: "bubble",
                },
              },
              modes: {
                bubble: {
                  size: 6,
                  distance: 40,
                },
              },
            },
          }}
        />
        v3.3.0
        <div
          style={{
            padding: "20px",
          }}
        >
          Your Intro to Crypto and Defi! Learn how Proof of Stake works!
        </div>
        <Link to="/create-account">Get Started</Link>
      </header>
    </div>
  );
};

function App() {
  const [accountList, setAccountList] = useState([]);

  const [connection, setConnection] = useState(null);

  useEffect(() => {
    const connectionToSolana = async () => {
      const conn = await new Connection(url, "recent");
      setConnection(conn);
    };
    connectionToSolana();
  }, []);

  const createCoin = async () => {
    const newAccount = await createAccount();
    setAccountList([...accountList, newAccount]);
  };

  return (
    <Router>
      <Switch>
        <Route path="/create-account">
          <CreateAccount
            connection={connection}
            createCoin={createCoin}
            accountList={accountList}
          />
        </Route>

        <Route path="/send-transactions">
          <SendTransaction
            connection={connection}
            createCoin={createCoin}
            accountList={accountList}
          />
        </Route>

        <Route path="/learn-rent">
          <LearnAboutRent
            connection={connection}
            createCoin={createCoin}
            accountList={accountList}
          />
        </Route>

        <Route path="/">
          <Home />
        </Route>
      </Switch>
    </Router>
  );
}

export default App;
