const fetchAllAcountInfo = async (publicKey, connection) => {
  if (publicKey) {
    const balance = await connection.getBalance(publicKey);

    const accountInfo = await connection.getAccountInfoAndContext(publicKey);

    return Promise.all(
      [balance, accountInfo].then((values) => {
        console.log(values);
        return values;
      }),
    );
  }

  return null;
};

export default fetchAllAcountInfo;
