import { Container, Row, Col } from "react-grid-system";
import AccountCard from "../Components/AccountCard";
import Link from "../Components/Link";
import createAccount from "../utils/createAccount";

const CreateAccount = ({ createCoin, accountList }) => {
  return (
    <Container className="wrapper">
      <Row>
        <Col>
          <h1>Let's learn about accounts!</h1>
          <div>
            <div>
              In most cryptos, it's very easy to make hundreds of accounts.
            </div>
            <div>
              Having a lot of accounts is useful for many reasons! You can have
              a cold storage account that you never touch and keep under your
              mattress, while also having a hot wallet account which you use to
              trade actively.
            </div>
            <div>Let's try creating a few accounts ourselves</div>
            <div>Click the button below to create an account!</div>
          </div>
        </Col>
      </Row>

      <Row>
        {accountList.map((account, index) => (
          <Col md={4} sm={6}>
            <AccountCard account={account} />Â¥
          </Col>
        ))}
      </Row>
      <div style={{ display: "flex" }}>
        <div>
          <button
            onClick={() => {
              createCoin();
            }}
            className="createAccount"
          >
            Create Account
          </button>
        </div>

        <Link
          to={`/send-transactions`}
          className={!accountList.length ? "disabled" : null}
        >
          Go Next
        </Link>
      </div>
    </Container>
  );
};

export default CreateAccount;
