import { useState } from "react";
import createAccount from "../utils/createAccount";

const CreateAccount = () => {
  const [accountList, setAccountList] = useState([]);

  const createCoin = () => {
    const newAccount = createAccount();
    setAccountList([...accountList, newAccount]);
  };

  return (
    <div className="wrapper">
      <h1>Let's learn about accounts!</h1>
      <div>
        <div>In most cryptos, it's very easy ... to and ...</div>
        <div>Click the button below to create an account!</div>
      </div>
      <div>
        <button onClick={() => createCoin()}>Create Coin</button>
      </div>
      {accountList.map((account, index) => (
        <li>
          <div>1</div>
        </li>
      ))}
    </div>
  );
};

export default CreateAccount;
