const AccountCard = ({ account = {} }) => {
  return (
    <div className="card">
      <div>
        {account?.publicKey && (
          <div>
            <h2>Public Key</h2>
            <div
              style={{
                wordWrap: "break-word",
              }}
            >
              {account.publicKey.toString()}
            </div>
          </div>
        )}
        {account?.secretKey && (
          <div>
            <h2>Secret Key</h2>
            <b
              style={{
                marginBottom: "20px",
              }}
            >
              Never share this key as someone can steal your funds
            </b>
            <div
              style={{
                wordWrap: "break-word",
              }}
            >
              {account.secretKey.toString()}
            </div>
          </div>
        )}
      </div>
      {account.balance && <div>{account.balance} </div>}
    </div>
  );
};

export default AccountCard;
