import { Explanation, ExplanationText, Title } from "../Components/Explanation";
import { BrowserRouter as Router, useHistory, Link } from "react-router-dom";
import { useEffect, useState } from "react";
import {
  Connection,
  Transaction,
  SystemProgram,
  PublicKey,
  Account,
  sendAndConfirmTransaction,
  sendAndConfirmRawTransaction,
} from "@solana/web3.js";

import fetchAllAcountInfo from "../utils/fetchAllAccountInfo";
import AccountCard from "../Components/AccountCard";
import Github from "../Components/Github";
import { Row, Col, Container } from "react-grid-system";

const SendTransaction = ({ accountList, connection }) => {
  const [transaction, setTransactions] = useState(false);
  const [value, setValue] = useState("");
  const [balanceLoading, setBalanceLoading] = useState(true);
  const [loading, setLoading] = useState(false);

  const sendTransactions = async (e) => {
    setLoading(true);
    const account = new Account(transaction?.[0].secretKey);

    const secondAccount = new PublicKey(transaction[1].publicKey.toString());

    const bloop = new Transaction().add(
      SystemProgram.transfer({
        fromPubkey: account.publicKey,
        toPubkey: secondAccount,
        lamports: value,
      }),
    );

    await sendAndConfirmTransaction(connection, bloop, [account], {
      skipPreflight: true,
    });

    const accountDetailOne = await fetchAllAcountInfo(
      account.publicKey,
      connection,
    );

    let accountDetailTwo = await fetchAllAcountInfo(secondAccount, connection);

    setTransactions([
      { ...accountDetailOne, secretKey: transaction?.[0]?.secretKey },
      accountDetailTwo,
    ]);

    setLoading(false);
  };

  useEffect(() => {
    const abortController = new AbortController();

    const getNewAccount = async () => {
      const secondAccount = await new PublicKey(
        "DoFxSedewimW1j8wz1LDhNUsqWs97aytqXkhFj8WBz8H",
      );

      if (connection) {
        setLoading(true);
        setBalanceLoading(true);

        const mainAccount = new PublicKey(
          accountList?.[0]?.publicKey.toString(),
        );

        const accountDetailOne = await fetchAllAcountInfo(
          mainAccount,
          connection,
        );

        let accountDetailTwo = await fetchAllAcountInfo(
          secondAccount,
          connection,
        );

        const airDrop = await connection.requestAirdrop(
          mainAccount,
          1000000000,
        );

        setTransactions([
          { ...accountDetailOne, secretKey: accountList?.[0]?.secretKey },
          accountDetailTwo,
        ]);

        const tryAgain = async (number = 10) => {
          const mainAccountUpdated = await fetchAllAcountInfo(
            mainAccount,
            connection,
          );
          if (abortController.signal.aborted) {
            return;
          }

          console.log(
            balanceLoading,
            mainAccountUpdated.balance,
            transaction?.[0]?.balance,
          );

          if (!number) {
            if (mainAccountUpdated.balance > transaction[0].balance) {
              setTransactions([
                {
                  ...mainAccountUpdated,
                  secretKey: accountList?.[0]?.secretKey,
                },
                accountDetailTwo,
              ]);

              setBalanceLoading(false);

              return;
            }
          } else {
            if (balanceLoading) {
              await new Promise((resolve) => setTimeout(() => resolve(), 2000));

              await tryAgain(airDrop, number - 1);
            }
          }
        };

        tryAgain();

        setLoading(false);
      }
    };

    getNewAccount();
    return () => {
      abortController.abort();
    };
  }, [connection]);

  return (
    <Container className="wrapper">
      <Explanation>
        <Title>Let's try sending a transaction now!</Title>
        <ExplanationText>
          Some money has been airdropped into your account! Being able to send
          and receive a transaction is really important. To do this, you need
          the reciever wallet, and the send wallet. This page has two generated
          accounts. If you want to check the transaction, you can see a list of
          all transactions here:
        </ExplanationText>
      </Explanation>

      <Row>
        <Col xs={5}>
          <AccountCard account={transaction[0]} />
        </Col>
        <Col xs={2}>
          <div
            style={{
              display: "flex",
              height: "100%",
            }}
          >
            <img
              style={{
                width: "50%",
                margin: "auto",
              }}
              src="Arrow_east.svg"
            />
          </div>
        </Col>
        <Col xs={5}>
          <AccountCard account={transaction[1]} loadingBalance={loading} />
        </Col>
      </Row>

      <div
        style={{
          display: "flex",
          width: "100%",
        }}
      >
        <input
          onChange={(e) => {
            setValue(e.target.value);
          }}
        ></input>
        <button onClick={sendTransactions}>Send coins to new account</button>
      </div>

      {/* 
      <div>
        As you can see there is a transaction fee associated with your
        transaction.
      </div> */}
      <Link to={`/success`} className={!accountList.length ? "disabled" : null}>
        Finish
      </Link>

      <Github />
    </Container>
  );
};

export default SendTransaction;
