import { BrowserRouter as Router, Switch, Route, Link } from "react-router-dom";
import CreateAccount from "./Pages/01_CreateAccount";
import { useEffect, useState } from "react";

import "./App.css";
import createAccount from "./utils/createAccount";
import SendTransaction from "./Pages/02_SendTransactions";
import LearnAboutRent from "./Pages/03_LearnAboutRent";
import { Connection } from "@solana/web3.js";
import { url } from "./consts/consts";

const Home = () => {
  return (
    <div className="App">
      <header className="App-header">
        <Link to="/create-account">
          Your Intro to Crypto and Defi! Learn how Proof of Stake works!
        </Link>
      </header>
    </div>
  );
};

function App() {
  const [accountList, setAccountList] = useState([]);

  const [connection, setConnection] = useState([]);

  useEffect(() => {
    const connectionToSolana = async () => {
      const conn = await new Connection(url, "recent");
      setConnection(conn);
    };
  }, []);

  const createCoin = async () => {
    const newAccount = await createAccount();
    setAccountList([...accountList, newAccount]);
  };

  return (
    <Router>
      <Switch>
        <Route path="/create-account">
          <CreateAccount createCoin={createCoin} accountList={accountList} />
        </Route>

        <Route path="/send-transactions">
          <SendTransaction createCoin={createCoin} accountList={accountList} />
        </Route>

        <Route path="/learn-rent">
          <LearnAboutRent createCoin={createCoin} accountList={accountList} />
        </Route>

        <Route path="/">
          <Home />
        </Route>
      </Switch>
    </Router>
  );
}

export default App;
