import { BrowserRouter as Router, Switch, Route, Link } from "react-router-dom";
import CreateAccount from "./Pages/01_CreateAccount";
import { useEffect, useRef, useState } from "react";

import "./App.css";
import createAccount from "./utils/createAccount";
import SendTransaction from "./Pages/02_SendTransactions";
import LearnAboutRent from "./Pages/03_LearnAboutRent";
import { Connection } from "@solana/web3.js";
import { url } from "./consts/consts";
import Particles from "react-particles-js";

const Home = () => {
  let ref = useRef();
  return (
    <div className="App">
      <Particles
        params={{
          fps_limit: 28,
          particles: {
            collisions: {
              enable: false,
            },
            number: {
              value: 200,
              density: {
                enable: false,
              },
            },
            line_linked: {
              enable: true,
              distance: 30,
              opacity: 0.4,
            },
            move: {
              speed: 1,
            },
            opacity: {
              anim: {
                enable: true,
                opacity_min: 0.05,
                speed: 1,
                sync: false,
              },
              value: 0.4,
            },
          },
          polygon: {
            enable: true,
            scale: 0.5,
            type: "inline",
            move: {
              radius: 10,
            },
            url: "https://rpj.bembi.dev/small-deer.2a0425af.svg",
            inline: {
              arrangement: "equidistant",
            },
            draw: {
              enable: true,
              stroke: {
                color: "rgba(255, 255, 255, .2)",
              },
            },
          },
          retina_detect: false,
          interactivity: {
            events: {
              onhover: {
                enable: true,
                mode: "bubble",
              },
            },
            modes: {
              bubble: {
                size: 6,
                distance: 40,
              },
            },
          },
        }}
      />

      <Particles
        params={{
          particles: {
            number: {
              value: 60,
              density: {
                enable: true,
                value_area: 1500,
              },
            },
            line_linked: {
              enable: true,
              opacity: 0.02,
            },
            move: {
              direction: "right",
              speed: 0.05,
            },
            size: {
              value: 1,
            },
            opacity: {
              anim: {
                enable: true,
                speed: 1,
                opacity_min: 0.05,
              },
            },
          },
          interactivity: {
            events: {
              onclick: {
                enable: true,
                mode: "push",
              },
            },
            modes: {
              push: {
                particles_nb: 1,
              },
            },
          },
          retina_detect: true,
        }}
      />
    </div>
    // <div className="App">
    //   <header className="App-header">
    //     <div
    //       style={{
    //         padding: "20px",
    //       }}
    //     >
    //       Your Intro to Crypto and Defi! Learn how Proof of Stake works!
    //     </div>
    //     <Link to="/create-account">Get Started</Link>
    //   </header>
    // </div>
  );
};

function App() {
  const [accountList, setAccountList] = useState([]);

  const [connection, setConnection] = useState(null);

  useEffect(() => {
    const connectionToSolana = async () => {
      const conn = await new Connection(url, "recent");
      setConnection(conn);
    };
    connectionToSolana();
  }, []);

  const createCoin = async () => {
    const newAccount = await createAccount();
    setAccountList([...accountList, newAccount]);
  };

  return (
    <Router>
      <Switch>
        <Route path="/create-account">
          <CreateAccount
            connection={connection}
            createCoin={createCoin}
            accountList={accountList}
          />
        </Route>

        <Route path="/send-transactions">
          <SendTransaction
            connection={connection}
            createCoin={createCoin}
            accountList={accountList}
          />
        </Route>

        <Route path="/learn-rent">
          <LearnAboutRent
            connection={connection}
            createCoin={createCoin}
            accountList={accountList}
          />
        </Route>

        <Route path="/">
          <Home />
        </Route>
      </Switch>
    </Router>
  );
}

export default App;
